ss = ""

total = 0
for i, _ in enumerate(ss):
  if i + 1 >= len(ss):
      continue
  c1, c2 = ss[i], ss[i+1]
  if c1 == c2:
    total += int(c1)
print(total)

bigtwo = ""

def halfway(s2):
    h1 = s2[:len(s2)//2]
    h2 = s2[len(s2)//2:]

    print(len(h1), len(h2))

    total = 0
    for c1, c2 in zip(h1, h2):
      if c1 == c2:
        total += int(c1)
    return 2 * total


print(halfway("1212"), "produces", 6)
print(halfway("1221"), "produces", 0)
print(halfway("123425"), "produces", 4)
print(halfway("123123"), "produces", 12)
print(halfway("12131415"), "produces", 4)
print(halfway(bigtwo))
